mapper.update:
update DOCUMENT_HOLIDAY as a set DOCUMENT_HOLIDAY_STATUS="결재완료"
,DOCUMENT_HOLIDAY_PAYMENTDAY=CURDATE()
,DOCUMENT_HOLIDAY_PAYMENTUSER=#{paymentName}
,DOCUMENT_HOLIDAY_PAYMENTDEPARTMENT=#{paymentDepartment}
,DOCUMENT_HOLIDAY_PAYMENTUSERID=#{paymentuserid}
where DOCUMENT_HOLIDAY_CHECK="check"

update DOCUMENT_TASKREPORT set DOCUMENT_TASKREPORT_STATUS="결재완료"
,DOCUMENT_TASKREPORT_PAYMENTDAY=CURDATE()
,DOCUMENT_TASKREPORT_PAYMENTUSER=#{paymentName}
,DOCUMENT_TASKREPORT_PAYMENTDEPARTMENT=#{paymentDepartment}
,DOCUMENT_TASKREPORT_PAYMENTUSERID=#{paymentuserid}
where DOCUMENT_TASKREPORT_CHECK="check"

update DOCUMENT_RESIGNATION set DOCUMENT_RESIGNATION_STATUS="결재완료"
,DOCUMENT_RESIGNATION_PAYMENTDAY=CURDATE()
,DOCUMENT_RESIGNATION_PAYMENTUSER=#{paymentName}
,DOCUMENT_RESIGNATION_PAYMENTDEPARTMENT=#{paymentDepartment}
,DOCUMENT_RESIGNATION_PAYMENTUSERID=#{paymentuserid}
where DOCUMENT_RESIGNATION_CHECK="check"

update DOCUMENT_HOLIDAY set DOCUMENT_HOLIDAY_STATUS="반려"
,DOCUMENT_HOLIDAY_PAYMENTDAY=CURDATE()
,DOCUMENT_HOLIDAY_PAYMENTUSER=#{paymentName}
,DOCUMENT_HOLIDAY_PAYMENTDEPARTMENT=#{paymentDepartment}
,DOCUMENT_HOLIDAY_PAYMENTUSERID=#{paymentuserid}
where DOCUMENT_HOLIDAY_CHECK="check"

update DOCUMENT_TASKREPORT set DOCUMENT_TASKREPORT_STATUS="반려"
,DOCUMENT_TASKREPORT_PAYMENTDAY=CURDATE()
,DOCUMENT_TASKREPORT_PAYMENTUSER=#{paymentName}
,DOCUMENT_TASKREPORT_PAYMENTDEPARTMENT=#{paymentDepartment}
,DOCUMENT_TASKREPORT_PAYMENTUSERID=#{paymentuserid}
where DOCUMENT_TASKREPORT_CHECK="check"

update DOCUMENT_RESIGNATION set DOCUMENT_RESIGNATION_STATUS="반려"
,DOCUMENT_RESIGNATION_PAYMENTDAY=CURDATE()
,DOCUMENT_RESIGNATION_PAYMENTUSER=#{paymentName}
,DOCUMENT_RESIGNATION_PAYMENTDEPARTMENT=#{paymentDepartment}
,DOCUMENT_RESIGNATION_PAYMENTUSERID=#{paymentuserid}
